import React from 'react';
import { View, TouchableOpacity, Text, TextInput, ActivityIndicator } from 'react-native';
import firebase from 'firebase';                  //ここ書き方修正
import 'firebase/firestore';
import { JSHash, JSHmac, CONSTANTS } from "react-native-hash";
// import { StyleSheet, Text, View ,Alert,SafeAreaView,TouchableOpacity} from 'react-native';
// import { Card,TextInput } from 'react-native-paper';
// import firebase from 'firebase/app';                   //ここ書き方修正
// import 'firebase/firestore';
// import { Button } from 'react-native-elements';
// import { Icon } from 'react-native-vector-icons/FontAwesome';
// import 'react-native-gesture-handler';
// import { NavigationContainer } from '@react-navigation/native';
// import { createStackNavigator } from '@react-navigation/stack';
// import LoginForm from './LoginForm';
// import HomeScreen from './HomeScreen';
// import { JSHash, JSHmac, CONSTANTS } from "react-native-hash";


const config = {
  apiKey: "AIzaSyBbxtMSlVYLpsa3KjdOgO6Myd2SPkirAb4",
  authDomain: "tsuntsun-c66c8.firebaseapp.com",
  databaseURL: "http://tsuntsun-c66c8.firebaseio.com",
  projectId: "tsuntsun-c66c8",
  storageBucket: "tsuntsun-c66c8.appspot.com",
  messagingSenderId: "20998065066",
};


// firebase.initializeApp(config);   //データベースの初期化
if (!firebase.apps.length) {         //この条件式がないと更新のたびに複数回呼び出してエラーがでる
    firebase.initializeApp(config);
}

const db = firebase.firestore();



class LoginForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
        email: '',
        password: '',
        error: '',
        loading: false,
        hash:''
    }
    this.hash = this.hash.bind(this);
}


  onButtonPress() {
    const { email, password } = this.state;
    this.setState({error: '', loading: true});

    firebase.auth().signInWithEmailAndPassword(email, password)
      .then((this.onLoginSuccess.bind(this)))
      .catch(() => {
        firebase.auth().createUserWithEmailAndPassword(email, password)
          .then((this.onLoginSuccess.bind(this)))
          .catch((this.onLoginFail.bind(this)));
      });
  }


hash(email,password){
  JSHmac(email,password, CONSTANTS.HmacAlgorithms.HmacSHA256)
  .then(hash => db.collection(hash).add({
      email:this.state.email,
      password: this.state.password,
  })
  .then(function(docRef) {
      console.log("Document written with ID: ", docRef.id);
  })
  .catch(function(error) {
      console.error("Error adding document: ", error);
  })
)
  .catch(e => console.log(e));
};

  onLoginSuccess() {
    this.setState({
      loading: false,
      error: ''
    });
    return(
    this.hash(this.state.email,this.state.password)
  )
  }

  onLoginFail() {
    this.setState({
      loading: false,
      error: 'Authentication Failed'
    });
  }

  loadSpinner() {
    if (this.state.loading) {
      return <ActivityIndicator size="small" />
    }

    return (
      <TouchableOpacity onPress={this.onButtonPress.bind(this)} style={styles.buttonStyle}>
        <Text>
          新規登録 or ログイン
        </Text>
      </TouchableOpacity>
    )
  }

  render() {
    return (
      <View>
        <View>
          <TextInput
              placeholder="user@gmail.com"
              autoCorrect={false}
              value={this.state.email}
              onChangeText={email => this.setState({ email })}
            />
        </View>
        <View style={styles.wrap}>
          <TextInput
              secureTextEntry
              placeholder="password"
              autoCorrect={false}
              value={this.state.password}
              onChangeText={password => this.setState({ password })}
            />
        </View>

        <View style={styles.wrap}>
          {this.loadSpinner()}
        </View>
      </View>
    )
  }
}

const styles = {
  // スタイルを記述
  wrap: {
    padding: 10
  },
  textStyle: {
    alignSelf: 'center',
    color: '#007aff',
    fontSize: 16,
    fontWeight: '600',
    paddingBottom: 10,
    paddingTop: 10
  },
  buttonStyle: {
    flex:1,
    alignSelf: 'stretch',
    backgroundColor: '#fff',
    borderRadius: 5,
    borderWidth: 1,
    borderColor: '#007aff'
  },
  inputStyle: {
    color: '#000',
    paddingRight: 5,
    paddingLeft: 5,
    fontSize: 18,
    lineHeight: 23,
    height: 30,
    borderWidth: 1,
    borderColor: '#333'
  }
}
export default LoginForm;
